void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 350.0;
    float timeDilation = -zoom*abs(sin(0.5*iTime))+zoom+0.5;

    float x0 = 2.0*fragCoord.x/(iResolution.y*timeDilation)-(2.0/timeDilation)-0.6843; // scale to lie on Mandelbrot x-set (-2, 0.47)
    float y0 = 2.0*fragCoord.y/(iResolution.y*timeDilation)-(1.0/timeDilation)+0.2888; // scale to lie on Mandelbrot y-set (-1.12, 1.12)

    float x = 0.0;
    float y = 0.0;

    int iteration = 0;
    int max_iteration = 8000;

    //while ((x*x + y*y) <= 70.0*abs(sin(iTime)+1.0) && iteration < max_iteration)
    while (((x*x + y*y) <= 4.0) && iteration < max_iteration)
    {
        float xtemp = x*x - y*y + x0;
        y = 2.0*x*y + y0;

        x = xtemp;

        iteration++;
    }

    if (iteration == max_iteration)
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    else
        fragColor = vec4((float(iteration)*2.0*x0)/20.0, (float(iteration)*2.0*y0)/(timeDilation+zoom*0.6), (float(iteration)*-y0)/15.0, 1.0);
}
