void mainImage(out vec4 c, in vec2 fc)
{
    vec2 uv = (2.0*fc.xy-iResolution.xy)/iResolution.y;

    vec3 color = vec3(0.0);

    vec4 color1 = vec4(0.4,0.4,0.3,1.0);
    vec4 color2 = vec4(0.1,0.1,0.1,1.0);

    color1*=.8-distance(uv,vec2(-.1,-.2));
    color2*=.6-distance(uv,vec2(.25,.3));
    vec4 sphere = color1+color2 ;

    float d = distance(uv, vec2(0.0));
    float t =1.0- smoothstep(.595,.61, d);
    sphere*=t+.2*uv.y;



    c = fract(sphere*1.4+sin(iTime)*0.02*60.0);
}
