void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;

  float d = length(uv-vec2(sin(iTime*2.)*.4,cos(iTime*1.)*.2));

  vec3 col = vec3(0);
  float brightness = 0.1 * sin(iTime*1.)+.2;
  col += brightness*.2 / d;
  col.y *= sin(iTime*1.);
  col.z *= sin(iTime*1.);
  fragColor = vec4(col, 1.);
}
