float speed=0.05;

void mainImage(out vec4 fragColor,in vec2 fragCoord){
	vec2 uv=fragCoord.xy/iResolution.xy;

	vec3 texcol;


    //if javascript event is 'mousedown'
	//float x=(iMouse.x*0.5-0.5)+(1.0-uv.x);
	//float y=(iMouse.y*0.5-0.5)+uv.y;

    float x=0.5-uv.x;
    float y=0.5-uv.y;

	float r=-(x*x+y*y);
	float z=1.0+0.5*sin((r+iTime*speed)/0.01);

	texcol.x=z;
	texcol.y=z;
	texcol.z=z;

	vec3 color=texture(iChannel0,uv).xyz;

	fragColor=vec4(pow(color,texcol),1.0);

}
