#define PI 3.14159265

#define V(y, k) (2. * cos(2. * PI * float(k) * (y)))

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    uv -= 0.5;

    float x = uv.x * 6.;
    float y = (uv.y*1.5+1.)/ 2.;

    int q = 100 + int(sin(iTime)*100.); // number of iterations

    float det = 0.;
    float det2 = 0.;
    float det1 = 1.;
    for (int j = 0; j < q; ++j) { // calculate determinant of tridiagonal matrix
        det = (V(y, j+1) - x) * det1 - det2;
        det2 = det1;
        det1 = det;
    }

    // Lyapunov exponent
    float f = log(abs(det)) / float(q);
    float c = clamp(f, 0., 0.3) * 9.5 - 1.5;

    vec3 col = vec3(
        sin(c), sin(c + PI / 3.), sin(c + 2. * (PI / 3.))
    );

    fragColor = vec4(col, 1.);
}
