float drawRect(vec2 pos, float w, float h, vec2 uv) {
    float left = step(pos.x - w*0.5,uv.x);
    float right = 1.0 - step(pos.x + w*0.5,uv.x);
    float bottom = step(pos.y - h*0.5,uv.y);
    float top = 1.0 - step(pos.y + h*0.5,uv.y);
    return left * right * bottom * top;
}

vec3 circle(vec2 pos, float r, vec2 uv) {
    vec2 center = pos;
    float dist = length(center - uv);
    float shade = step(r, dist);
    return vec3(1.0 - shade);
}

vec3 line(float w, vec2 uv) {
    float shade = step(1.0 - w, sin(uv.y*3.14));
    return vec3(shade);
}

mat2 rotate(float a) {
    return mat2(cos(a),sin(a), -sin(a),cos(a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;
    vec2 uv = st;
    uv = rotate(0.7) * uv;
    uv = fract(uv * 100.0);

    vec3 tex_color = texture(iChannel0, st).rgb;

    vec3 col = vec3(0.1,0.4,0.6);

    //col = vec3(drawRect(vec2(0.4,0.6),0.3,0.5,uv));

    float brightness = tex_color.r*0.3 + tex_color.g*0.59 + tex_color.b*0.11;
    //col = circle(vec2(0.5), brightness * 0.5, uv); 

    col = line(brightness, uv);

    fragColor = vec4(col,1.0);
}
