void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float top = 0.75;
    float topLeft = 0.484;
    float leftTop = 0.645;
    float leftBottom = 0.59;
    float topRight = 0.516;
    float rightTop = 0.70;
    float rightBottom = 0.58;
    float bottom = 0.25;
    float right = 0.6;
    float left = 0.4;
    vec3 col;
    vec2 animation1 = vec2(0.25, -1.0 + mod(iTime/4.0, 2.0));
    vec2 animation2 = vec2(0.75, -1.0 + mod(iTime/4.0, 2.0));
    vec2 animation3 = vec2(0.60, -1.0 + mod(iTime/4.0, 2.0));
    vec2 animation4 = vec2(0.40, -1.0 + mod(iTime/4.0, 2.0));
    //inside cross
    if(uv.y < top && uv.y > bottom && uv.x < right && uv.x > left &&
            ((uv.x > topLeft && uv.x < topRight) || (uv.y < leftTop && uv.y > leftBottom))){
        //black shaded part
        if(length(uv.x - 0.5) > 0.01 && length(uv.y - 0.4) < 0.2){
            col = vec3(0,0,0);
        }
        else if(length(iTime + uv.x + 0.5) > 0.01 ){
            //squiggly part
            if(uv.y < sin(500.0 * uv.x - iTime * 10.0) * 0.01 + 0.62){
                 col = vec3(0.5,0,0.5);
            }
            else{
                col = vec3(1.0,0,1.0);
            }
        }
    }
    else{
        //background
        if(uv.y > length(cos((iTime/4.0 + uv.xyx)*1.5)) - 0.7){
            col = vec3(1.0,0.8,0);
        }
        else{
            col = vec3(0.10,0.30,0.5);
        }
    }
    //noise
    vec4 texture1 = texture(iChannel0, uv*mod(iTime/4.0, -10.0));
    // Output to screen
    fragColor = vec4(col.rg, texture1);
}
