// ported from https://lettier.github.io/3d-game-shaders-for-beginners/dilation.html

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    vec2 texSize = iResolution.xy;

    float bokehSize = 6.0;
    float separation = 3.0 * ( sin( iTime ) * 0.5 + 0.5);
    float mx = 1.0;
    float minThreshold = 0.3;
    float maxThreshold = 0.7;   

    fragColor = vec4( texture( iChannel0, fragCoord / texSize ) );

    vec4 cmx = fragColor;

    for( float i = -bokehSize; i <= bokehSize; ++i ) {

        for( float j = -bokehSize; j <= bokehSize; ++j ) {

             if (!(distance(vec2(i, j), vec2(0, 0)) <= bokehSize)) { continue; }

             vec4 c = texture( iChannel0, ( gl_FragCoord.xy + (vec2(i, j) * separation) ) / texSize );

             float brightness = dot(c.rgb, vec3(0.21, 0.72, 0.07));             

             if (brightness > mx) {
                mx  = brightness;
                cmx = c;
             }

        }

    }

    fragColor.rgb =
    mix
      ( fragColor.rgb
      , cmx.rgb
      , smoothstep(minThreshold, maxThreshold, mx)
      );
}
