void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 position = fragCoord/iResolution.xy;
    float glow_length=cos(iTime*4.+position.x*3.)*.03+.05;
    float green=0.;

    green=1.-position.y/glow_length+(1./glow_length/2.);
    if(position.y<.5){
      green=position.y/glow_length-(1./glow_length/2.-1.);
    }

    // Output to screen
    fragColor = vec4(0.,green,0.,1.);
}
