// CLICK MOUSE AND MOVE
void mainImage( out vec4 color, in vec2 o )
{
    o-=iResolution.xy/vec2(2.0);
    o/=iResolution.y/vec2(7.0);

    float x = (mod(abs(o.x),100.0));
    float y = (mod(abs(o.y),100.0));

    vec2 m;
    if(iMouse.x*iMouse.y<=1.0){
        m+=iResolution.xy/2.0;
    }else m = iMouse.xy;

    float mx = (-iResolution.x/2.0+m.x)/iResolution.x*8.0;
    float my = (-iResolution.y/2.0+m.y)/iResolution.y*8.0;

    float R = x*x+y*y;
    float L=pow(o.x+(mx)/5.0,2.0)+pow(o.y+(my)/5.0,2.0);

    float t = mod(1000.0+iTime,x/y);
    float f = mod(1000.0+iTime,y/x);

    float r1 = f*sin(x+sin(x+y)*L)+f*cos(y-sin(x-y)*L);
    float r2 = t*sin(x+sin(y-x)*L)+t*cos(y-sin(-y-x)*L);   

    color = vec4(
        mod(cos(r1),cos(r2)),
        mod(cos(r1),sin(r2)),
        max(sin(r2),sin(r1)),
        //mod(cos(r1),cos(r2)),
        //1.5*mod(cos(r1),sin(r2)),
        //abs(sin(r2)*sin(r1)),
    0)/sin(pow(R,0.5));
}
