void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - 0.5; // 坐标转换到-0.5 to 0.5

    // 变量声明
    vec3 col = vec3(0,0,0); 

    float x = uv.x;
    float y = uv.y;

    // 线性方程的判断，根据斜率来画三角形
    if (y > x && y > -x) {
        // Top
        col = vec3(1,0,0);
    } else if (y > -x ) {
        // Right
        col = vec3(0,1,0);
    } else if (y > x) {
        // Left
        col = vec3(0,0,1);
    } else  {
        // Bottom
        col = vec3(1,1,1);
    }

    // 颜色输出到屏幕
    fragColor = vec4(col,1.0);
}
