#define R                           iResolution
#define TO_CENTERED_NDC(SC)       ( ((SC)+(SC)-R.xy) / R.y )         // in range x=[-AR; AR], y=[-1.0; 1.0]
#define NZMX                      ( (iMouse.x+iMouse.x-R.x) / R.x )  // in range [-1.0; 1.0]
#define NZMY                      ( (iMouse.y+iMouse.y-R.y) / R.y )  // in range [-1.0; 1.0]
#define LMB_IS_HOLD               ( iMouse.z > 0.0 )
#define INITIALLY_FAR               9e30

float Linf(vec2 B, vec2 E)
{
    vec2 D = E - B;
    return max(abs(D.x), abs(D.y));
}

/* Solution from https://www.desmos.com/calculator/zxvcxqmubu */
#define P(t) (P0*(1.-t)*(1.-t)*(1.-t)+3.*P1*t*(1.-t)*(1.-t)+3.*P2*t*t*(1.-t)+P3*t*t*t)
float cubicBezierSegmentSDF_Linf(
    vec2 NDC,
    vec2 P0,
    vec2 P1,
    vec2 P2,
    vec2 P3)
{
    float minSqDist = INITIALLY_FAR;

    vec2 A =   -P0+3.*P1-3.*P2+P3,
         B = 3.*P0-6.*P1+3.*P2,
         C =      -3.*P0+3.*P1,
         D =         P0-NDC;

    const int N = 4;
    const int M = 4;
    float coef[N*M] = float[N*M](
          A.x+A.y, B.x+B.y, C.x+C.y, D.x+D.y,
          A.x-A.y, B.x-B.y, C.x-C.y, D.x-D.y,
              0.0,  3.*A.x,  2.*B.x,     C.x,
              0.0,  3.*A.y,  2.*B.y,     C.y
    );

    vec2 T[3];

    for (int eq = 0; eq < N; ++eq)
    {
        float coef_[4] = float[4](
            coef[eq*M], coef[eq*M+1], coef[eq*M+2], coef[eq*M+3]
        );

        int num = solve(T, coef_);

        for (int i = 0; i < num; ++i)
        {
            float p = clamp(T[i].x, 0.0, 1.0);

            minSqDist = min(minSqDist, Linf(P(p), NDC));
        }
    }

    //minSqDist = min(minSqDist, Linf(P(0.5), NDC));

    float R = sinOSC(0.0, 0.3, iTime);
    return minSqDist - R;
}

/* https://www.shadertoy.com/view/ddfGz2 */
vec3 colorize_field(float dist, vec3 color)
{    
    float F = 30., freq = F*dist, aa = 1./F, C = 0.7, amp = 0.2, width = F*0.01, //0.3
          waves_sdf = C + amp*smoothstep(8./iResolution.y, 0., (width+cos(freq)) * aa),
          exp_raise = 1. - exp(-4.*abs(dist));

    color = 1.0 - sign(dist) * color; // Change color if dist is negative. 
    color *= exp_raise;               // Attenuate color around edges.     
    color *= waves_sdf;               // Add waves.

    return mix(color, vec3(1), smoothstep(8./iResolution.y, 0., abs(dist)) );
}

vec3 add_metric_circle(
    vec2 NDC,
    vec2 P0,
    vec2 P1,
    vec2 P2,
    vec2 P3,
    vec3 color)
{
    float unit = 7.0 / R.y;

    vec2 M = 
        LMB_IS_HOLD ?
            TO_CENTERED_NDC(iMouse.xy) * 2.0 :
            rotateAroundZ(vec2(1, 0), iTime * 0.5) * 1.0;
            //vec2(0);

    //M=vec2(0.5, 1.5);

    // Add point
    color = mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, length(NDC - M) - 0.008));

    float dist_form_point_to_box_edge = Linf(NDC, M) - abs(cubicBezierSegmentSDF_Linf(M, P0, P1, P2, P3));
    float outline = abs(dist_form_point_to_box_edge);

    // Add metric circle
    return mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, outline));
}
/* IQ coloring scheme */

void mainImage( out vec4 O, in vec2 SC )
{
    float offs = sinOSC(-0.5, 0.5, iTime * 0.5);
    vec2 NDC = TO_CENTERED_NDC(SC) * 3.0;

    vec2 P0 = vec2(2.0, 0.0) * 0.65; 
    vec2 P1 = vec2(8.0, -3.0);
    vec2 P2 = vec2(-3.0, 3.0);
    vec2 P3 = vec2(-2.0, 0.0) * 0.65;

    P0 = rotateAroundZ(P0, iTime * 0.1) + vec2(2.*offs, -offs);
    P1 = rotateAroundZ(P1, iTime * 0.9) + vec2(-12.*offs, 0.0);
    P2 = rotateAroundZ(P2, iTime * 0.9) + vec2(-8.*offs, 3.*offs);
    P3 = rotateAroundZ(P3, iTime * 0.1) + vec2(-offs*offs, offs);

    float minDist = cubicBezierSegmentSDF_Linf(NDC, P0, P1, P2, P3);

    // Blending.
    vec3 color = colorize_field(minDist, vec3(0.1, 0.4, 0.7));
    color = add_metric_circle(NDC, P0, P1, P2, P3, color);

    O = vec4(color, 1.0);
}

/* Returns sine modulated value in range [min, max]. */
float sinOSC(in float min, in float max, in float T)
{
    float v = (sin(T) + 1.0) * 0.5; // map T to [0.0, 1.0];
    return min + v * (max - min);   // map T to [min, max];
}

vec2 rotateAroundZ(vec2 p, float a)
{
    float s = sin(a);
    float c = cos(a);

    return vec2(
        p.x * c - p.y * s,
        p.y * c + p.x * s);
}

/*
    Calculates cube root with Newton-Raphson method.
*/
float cubeRoot(const float x)
{
    float xc = x;
    float xxc = x * x;

    for (int i = 0; i < 7; ++i)
    {
        xc = (2.0 * xc + x / xxc) * 0.33333333333333331;
        xxc = xc * xc;
    }

    return xc;
}

/*
    Return value:
        0 - two complex roots;
        1 - one real root, but root[0] == root[1];
        2 - two real roots;
*/
int solveQuadratic(
    inout vec2[2] roots,
    in float[3] coefs)
{
    float discriminant = coefs[1] * coefs[1] - 4.0 * coefs[0] * coefs[2];

    if (discriminant < 0.0)
    {
        float INVa = 1.0 / (2. * coefs[0]);
        float SQRT = sqrt(-discriminant);

        roots[0].x = -coefs[1] * INVa;
        roots[0].y = SQRT * INVa;
        roots[1].x = -coefs[1] * INVa;
        roots[1].y = -SQRT * INVa;

        return 0;
    }

    if (abs(discriminant) < 1e-14)
    {
        float Re = -coefs[1] / (2. * coefs[0]);

        roots[0].x = Re;
        roots[0].y = 0.0;
        roots[1].x = Re;
        roots[1].y = 0.0;

        return 1;
    }

    float INVa = 1.0 / (2. * coefs[0]);
    float SQRT = sqrt(discriminant);

    roots[0].x = (SQRT - coefs[1]) * INVa;
    roots[0].y = 0.0;
    roots[1].x = (-coefs[1] - SQRT) * INVa;
    roots[1].y = 0.0;

    return 2;
}

/*
    Return value:
        0 - no roots when A==0, B==0, C==0;
        1 - one real, two complex roots;
        2 - three real roots, but root[1] == root[2];
        3 - three real roots.
*/
int solveCubicVieta(
    inout vec2[3] roots,
    in float[4] coef)
{
    const float oneThird = 0.33333333333333331;
    const float TAU = 6.2831853071795862;

    float a = coef[1] / coef[0];
    float b = coef[2] / coef[0];
    float c = coef[3] / coef[0];

    float Q = (a * a - 3.0 * b) / 9.0;
    float R = (2.0 * a * a * a - 9.0 * a * b + 27.0 * c) / 54.0;

    float S = Q * Q * Q - R * R;

    if (S > 0.0)
    {
        float phi = oneThird * acos(R / sqrt(Q * Q * Q));
        float Re = -2.0 * sqrt(Q);

        roots[0].x = Re * cos(phi) - a * oneThird;
        roots[0].y = 0.0;
        roots[1].x = Re * cos(phi + TAU * oneThird) - a * oneThird;
        roots[1].y = 0.0;
        roots[2].x = Re * cos(phi - TAU * oneThird) - a * oneThird;
        roots[2].y = 0.0;

        return 3;
    }

    if (S < 0.0 && Q > 0.0)
    {
        float phi = oneThird * acosh(abs(R) / sqrt(Q * Q * Q));
        float signR = R < 0.0 ? -1.0 : 1.0;

        float sqrtQ = sqrt(Q);
        float Re = signR * sqrtQ * cosh(phi);
        float Im = sqrtQ * sinh(phi);
        float sqrt3 = 1.7320508075688772;

        roots[0].x = -2.0 * Re - a * oneThird;
        roots[0].y = 0.0;
        roots[1].x = Re - a * oneThird;
        roots[1].y = sqrt3 * Im;
        roots[2].x = Re - a * oneThird;
        roots[2].y = -sqrt3 * Im;

        return 1;
    }

    if (S < 0.0 && Q < 0.0)
    {
        float phi = oneThird * asinh(abs(R) / sqrt(abs(Q * Q * Q)));
        float signR = R < 0.0 ? -1.0 : 1.0;

        float sqrtQ = sqrt(abs(Q));
        float Re = signR * sqrtQ * sinh(phi);
        float Im = sqrtQ * cosh(phi);
        float sqrt3 = 1.7320508075688772;

        roots[0].x = -2.0 * Re - a * oneThird;
        roots[0].y = 0.0;
        roots[1].x = Re - a * oneThird;
        roots[1].y = sqrt3 * Im;
        roots[2].x = Re - a * oneThird;
        roots[2].y = -sqrt3 * Im;

        return 1;
    }

    if (Q == 0.0)
    {
        float Re = -cubeRoot(c - a * a * a / 27.0) - a / 3.0;
        float Im = sqrt(abs(a - 3.0 * Re) * (a + Re) - 4.0 * b) * 0.5;

        roots[0].x = Re;
        roots[0].y = 0.0;
        roots[1].x = -(a + Re) * 0.5;
        roots[1].y = Im;
        roots[2].x = -(a + Re) * 0.5;
        roots[2].y = Im;

        return 1;
    }

    float signR = R < 0.0 ? -1.0 : 1.0;
    float Re = signR * sqrt(Q);

    roots[0].x = -2.0 * Re - a * oneThird;
    roots[0].y = 0.0;
    roots[1].x = Re - a * oneThird;
    roots[1].y = 0.0;
    roots[2] = roots[1];

    return 2;
}


int solve(
    inout vec2[3] roots,
    in float[4] coef)
{
    float EPSILON = 1e-2;

    // Quadratic equation?
    if (abs(coef[0]) < EPSILON)
    {   
        // Linear equation?
        if (abs(coef[1]) < EPSILON)
        {
            // Constant?
            if (abs(coef[2]) < EPSILON) { return 0; }

            // Solve linear.
            roots[0].x = -coef[3]/coef[2];
            roots[0].y = 0.0;

            return 1;
        }

        vec2 r[2];
        float c[3] = float[3](
            coef[1],
            coef[2],
            coef[3]
        );

        int num = solveQuadratic(r, c);
        roots[0] = r[0];
        roots[1] = r[1];

        return num;
    }

    return solveCubicVieta(roots, coef);
}
