// from: https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}


float noise(vec2 n) {
	const vec2 d = vec2(0.0, 1.0);
  vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
	return mix(mix(rand(b), rand(b + d.yx), f.x), mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}




float randNorm(vec2 p){
  return mix(noise(p*150.), .5, .5);
}

// from: https://www.shadertoy.com/view/3ld3Wl
vec3 genNormMap( vec2 p )
{
    vec2 texelsize = vec2(.001);

    float dx = 0.;
    float dy = 0.;

    dx -= randNorm(vec2(p.x - texelsize.x, p.y - texelsize.y)) * 1.0;
	dx -= randNorm(vec2(p.x - texelsize.x, p.y              )) * 2.0;
	dx -= randNorm(vec2(p.x - texelsize.x, p.y + texelsize.y)) * 1.0;
	dx += randNorm(vec2(p.x + texelsize.x, p.y - texelsize.y)) * 1.0;
	dx += randNorm(vec2(p.x + texelsize.x, p.y              )) * 2.0;
	dx += randNorm(vec2(p.x + texelsize.x, p.y + texelsize.y)) * 1.0;

    dy -= randNorm(vec2(p.x - texelsize.x, p.y - texelsize.y)) * 1.0;
	dy -= randNorm(vec2(p.x              , p.y - texelsize.y)) * 2.0;
	dy -= randNorm(vec2(p.x + texelsize.x, p.y - texelsize.y)) * 1.0;
	dy += randNorm(vec2(p.x - texelsize.x, p.y + texelsize.y)) * 1.0;
	dy += randNorm(vec2(p.x              , p.y + texelsize.y)) * 2.0;
	dy += randNorm(vec2(p.x + texelsize.x, p.y + texelsize.y)) * 1.0;

    float nx = dx;
    float ny = dy;

    vec3 norm = vec3(nx,
                     ny,
                    sqrt(1.0 - nx*nx - ny*ny));

    return vec3(norm * vec3(0.5, 0.5, 1.0) + vec3(0.5, 0.5, 0.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec3 col = texture(iChannel0, uv).rgb;
    vec3 norm = normalize(genNormMap(uv) * 2. - 1.);
    vec3 lightDir = vec3(sin(iTime), cos(iTime), -1.0);

    float light = 0.5 + dot(-lightDir, norm) * 0.5;
    col *= light;


    // Output to screen
    fragColor = vec4(col,1.0);
}
