// How much to brighten the image to compensate for the brightness loss due to removing 3/4ths of the image brightness.
#define BRIGHTNESS_CORRECTION (1.333333)

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec3 colorIn = texture(iChannel0, uv).xyz;

    int rgbIndex = int(fragCoord.x) % 3;
    vec3 mask = vec3(rgbIndex == 0, rgbIndex == 1, rgbIndex == 2);

    vec3 colorOut = colorIn * mask * BRIGHTNESS_CORRECTION;

    fragColor = vec4(colorOut, 1.0);
}
