vec2 getHash12(float t) {
    float x = fract(sin(t*343.23)*788.22);
    float y = fract(sin((t+x)*547.34)*345.23);

    return vec2(x,y);
}

vec3 getGlowingColor(
    vec2 uv,
    float glowRange,
    float glowStart,
    float brightness
) {
    vec3 color = vec3(.0, glowStart / (20. / glowRange) * 3., .05);
    float farFromCenter = length(uv);

    color += brightness/farFromCenter;

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float centerSlope = .5;
    float starsClusterDensity = 90.;
    vec2 uv = (fragCoord-centerSlope*iResolution.xy)/iResolution.y;
    vec3 color = vec3(0);
    float time = sin(iTime/2.);

    for (float index = 0.; index < starsClusterDensity; index++) {
        vec2 direction = getHash12(index)-centerSlope;
        vec3 glowingColor = getGlowingColor(
            uv-direction*time+time/20.,
            .05, .1-time, .005
        );
        color += glowingColor/sin(iTime/2.)/60.;
    }


    fragColor = vec4(color,1.0);
}
