// Forked from https://www.shadertoy.com/view/3dtBDN
//   by https://www.shadertoy.com/user/Xor
//
// Formula by ern0
//   (x-7.5+sin(t)*5)**2+(y-7.5+cos(t*1.7)*5)**2-8
//   https://tixy.land/?code=%28x-7.5%2Bsin%28t%29*5%29**2%2B%28y-7.5%2Bcos%28t*1.7%29*5%29**2-8
// 

//Recreated @aemkey's "Tixy Land" in ShaderToy using shader syntax.
//Play around with the original here: https://tixy.land

// You can fiddle with dot count here:
#define count 32.
// Change ball size:
#define ballsize 24./count
// Set border (should be auto):
#define border count/8.

#define area (count/2.-border)
#define center ((count-1.)/2.)
#define tixy(t,i,x,y) \
    (x-center+sin(t)*area) \
    *(x-center+sin(t)*area) \
    /(ballsize) \
    + \
    (y-center+cos(t*1.7)*area) \
    *(y-center+cos(t*1.7)*area) \
    /(ballsize) \
    - \
   (count/2.)

//#define tixy(t,i,x,y) (x-7.5+sin(t)*5.)*(x-7.5+sin(t)*5.)+(y-7.5+cos(t*1.7)*5.)*(y-7.5+cos(t*1.7)*5.)-8.


//Render with the result:
void mainImage( out vec4 color, in vec2 coord )
{
    float s = 1.2/iResolution.y;
    vec2 u = (coord-.5*iResolution.xy)*s;
    u.y = -u.y;

    float square = step(abs(u.x),.5)*step(abs(u.y),.5);
    vec2 cell = floor((u+.5)*count);
    float index = cell.x+cell.y*count;
    float tixel = float(tixy(iTime,index,cell.x,cell.y));

    float dist = length(fract(u*count)-.5)*count/.5;
    float radius = count*min(abs(tixel),1.);

    vec3 col = tixel<0. ? vec3(255,34,68)/255. : vec3(1);
    col *= clamp((radius-dist)/s/count/count/2.,0.,1.)*square;

    color = vec4(col,1);
}
