mat2 rot(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c, s, -s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.yy + 0.5;

    // softly zoom
    uv *= mix(0.8, 2.0, sin(iTime*0.2+(4.14))*0.5+0.5);

    // softly rotate
    uv *= rot(mix(-5., -6., sin(-iTime*0.102)*0.5+0.5));

    // varying everything with noise and time is good right?! :D
    float PI = mix(3.14, 4.28, texture(iChannel0, vec2(iTime*0.01,0.)).x);

    vec3 col = vec3(uv.y);

    float offx = iTime*3.0;
    float offy = cos(uv.x*5.-iTime)*2.0;
    offy += iTime*5.0;

    float x = uv.x*PI*4.+offx;
    float y = uv.y*PI*4.+offy;

    col = vec3(0.2);
    col = mix(col, vec3(0.9,.9,.9), ceil(cos(x-iTime))    * ceil(cos(y)));
    col = mix(col, vec3(0.9,.7,.7), ceil(cos(x+PI+iTime)) * ceil(cos(y+PI)));
    col = mix(col, vec3(0.9,.3,.4), ceil(cos(y-iTime))    * ceil(cos(x)));
    col = mix(col, vec3(0.5,.2,.6), ceil(cos(y+PI+iTime)) * ceil(cos(x+PI)));


    // Output to screen
    fragColor = vec4(col,1.0);
}
