void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.y *= 2.0;

    if(uv.y >= 1.0)
    {
        uv.y -= 1.0;
        uv.y = 1.0 - uv.y;

        vec4 soundtexel0 = texture(iChannel0, vec2(uv.x, 0));
        vec4 soundtexel1 = texture(iChannel0, vec2(uv.x, 1));
        if(uv.y < soundtexel0.r)
        {
            fragColor.r = 1.0;
        }

        if(uv.y < soundtexel1.r)
        {
            fragColor.b = 1.0;
        }
    }
    else
    {
        vec4 soundtexel0 = texelFetch(iChannel0, ivec2(int(uv.x * iChannelResolution[0].x), 0), 0);
        vec4 soundtexel1 = texelFetch(iChannel0, ivec2(int(uv.x * iChannelResolution[0].x), 1), 0);
        if(uv.y < soundtexel0.r)
        {
            fragColor.r = 1.0;
        }

        if(uv.y < soundtexel1.r)
        {
            fragColor.b = 1.0;
        }
    }

}
