#define tex(a) texture(iChannel0,a).rgb
//he is my god, jean claude van damme save the queen
void mainImage( out vec4 C, in vec2 P )
{
    vec2 v = (P/iResolution.xy);
    vec4 c = texture(iChannel0,v);
    float x,y,i;
    for (i = 10.;i < 100.;i++) {
        x += distance(tex(vec2(v.x-1./i,v.y)),tex(vec2(v.x+1./i,v.y)));
        y += distance(tex(vec2(v.x,v.y-1./i)),tex(vec2(v.x,v.y+1./i)));
    }
    c += vec4(x,y,x+y,1.)/i;
    C = vec4(c);
}
