void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;    
    //float aspect = iResolution.x / iResolution.y;

    uv = vec2(uv.x - .5, uv.y - .5);  

    float w = .0001;
    float x = smoothstep(-w, w, uv.x);
    float y = smoothstep(-w, w, uv.y);
    vec3 col = vec3(x, y, 0);    
    fragColor = vec4(col,1.0);    

    float t = iTime * .1;
    float pi2 = 6.28;

    vec2 p2 = vec2(cos(t * pi2), sin(t * pi2));
    float phase = pi2 * .25;
    vec2 p1 = vec2(cos(t * pi2 - phase), sin(t * pi2 - phase));
    //vec2 p1 = vec2(0,0);
    p2 *= .25;
    p1 *= .25;

    vec2 dif1 = p2 - p1;
    vec2 dif2 = uv - p1;    
    float line = dif1.x * dif2.y - dif1.y * dif2.x;    

    float w1 = -0.06; 
    if(line > w1)// && line < w1)
    {
        fragColor = vec4(1.0 - fragColor.x, 1.0 - fragColor.y, 1.0 - fragColor.z, 1.0);
    }  

    w1 = 0.12; 
    if(line > w1)// && line < w1)
    {
        fragColor = vec4(1.0 - fragColor.x, 1.0 - fragColor.y, 1.0 - fragColor.z, 1.0);
    }  





    float point2 = smoothstep(.02, 0.019, length(uv - p2));
    fragColor += vec4(point2, point2, point2, 1.0);

    float point1 = smoothstep(.02, 0.019, length(uv - p1));
    fragColor += vec4(point1 * .8 , point1 * .5, point1 * .2, 1.0);
}
