void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 screen = fragCoord.xy/=iResolution.xy;
    vec3 gridColor = vec3(0.0, 0.0, 1.0);
    vec3 axesColor= vec3(0.0,0.0,0.0);
    vec3 bgColor = vec3(1.0);

    const float tickWidth= 0.1;
    vec3 pixelColor =bgColor;


    for(float i=0.0; i<0.1; i+=tickWidth)
    {
        if (abs(screen.x -i) <0.002 ||abs(screen.y -i) <0.002)
        {
            pixelColor=gridColor;
        }
    }
    fragColor= vec4(pixelColor, 1.0);


}
