void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;
    U *= fract(U*sin(iTime/10.)*3.);
    vec4 M = fract(texture(iChannel0, U, 99.)) / sin(U.y)/10.,            // mean RGB
         Q = texture(iChannel1, U, 99.),            // 2ns moments RGB
         S = sqrt(Q-M*M);                           // std-dev RGB

    O = .5 + ( texture(iChannel0,U) - M ) / (2.*S); // normalization
    O = sqrt(O);                                    // RGB -> sRGB
}
