vec3 scarlet = vec3(.7, 0.1, 0.);
const float PI = 3.1415;

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

mat2 rot2d(float a) {
    return mat2(
        vec2(cos(a),-sin(a)),
        vec2(sin(a), cos(a))
    );
}

vec3 bg(vec2 uv) {

    float darkness = mod(.1-uv.x, 0.1)*.4 + mod(uv.y, 0.1)*.2;
    return vec3(darkness);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
    uv*=rot2d(3.14*-.25);

    uv-=vec2(.7-iTime*.5 - sin(iTime + 1.5) * .9, -iTime*.5 - cos(iTime) * .1);

    vec3 col = bg(uv);
    float t = iTime;


    for(int i = 0; i < 400; i++) {       
        if(abs(uv.y) < 0.2 && uv.x < min(1., t) && uv.x > 0.) {
            col = scarlet - vec3(texture(iChannel0, uv).r, 0., 0.) * .08;
        }
        if(i % 2 == 0) {
            uv*=rot2d(PI*-.5);
            uv.y+=.8;
            uv.x-=.2;
        }else{
            uv*=rot2d(PI*.5);
            uv.y-=.8;
            uv.x-=.2;
        }
        t-=1.;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
