float u_BandW =2.0;//y方向的厚度
float u_OffsetX =40.0;//最大x偏移的长度（单位：个像素）
float u_Intensity =0.25;

float random (vec2 st)
{
    return fract(sin(dot(st.xy, vec2(12.9898,78.233))) * 43758.5453123);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 srcColor = texture(iChannel0, uv);

    vec4 res = srcColor;

    if(mod(fragCoord.y,u_BandW*2.0)<u_BandW-0.001)//[0,1,2x,3x]
    {
        float m_random = random(vec2(uv.yy));
        float uv_offsetx = u_OffsetX/iResolution.x;

        uv_offsetx = mix(uv_offsetx, uv_offsetx *m_random*u_Intensity,1.0);
        vec2 uv1 = uv+vec2(uv_offsetx,0.0);
        res = texture(iChannel0, uv1);
    }
    res.a = srcColor.a;
    fragColor = res;
}
