float gsblur(float x,float y,float sigma){
    return float(1./(2.*3.1415926*sigma*sigma))*exp(-((x*x+y*y)/(2.*sigma*sigma)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    float blur = max(5.,floor(iMouse.x/100.)+5.);

    float boxma = 1./ (blur*blur*4.);

    float sigma = blur/2.5;  

    float gaussblur = clamp(sign(uv.x-0.33333)-sign(uv.x-0.66666),0.,1.);
    float errorblur = clamp(sign(uv.x-0.66666),0.,1.); 


    vec4 boxcolor= vec4(0.);
    vec4 gausscolor= vec4(0.);
    vec4 errorcolor= vec4(0.);//一个做kawase做错的模糊 kawase得多次pass 效果还可以就留下了


    for(float i=-blur;i<blur;i++){
       for(float l=-blur;l<blur;l++){

          vec2 exy = vec2(i,l)/iResolution.xy;
          float weight = gsblur(i,l,sigma);


          boxcolor += texture(iChannel0,uv + exy)*boxma;
          gausscolor+= texture(iChannel0, uv + exy)*weight;



        }
        vec2 just = (vec2(i,i)/iResolution.xy)-((1./iResolution.xy)*0.5);
        vec2 aga = (vec2(i,-i)/iResolution.xy)-((1./iResolution.xy)*0.5);
        float weight = 1./(blur*2.*2.);


        errorcolor += texture(iChannel0, uv+just)*weight;
        errorcolor += texture(iChannel0, uv+aga)*weight;
    }



    vec4 color = vec4(0.);

    color = mix(mix(boxcolor ,gausscolor,gaussblur),errorcolor,errorblur);

    fragColor =color;
}
