void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv *= 1.0;
    // Time varying pixel color
    vec3 col = 0.5 + 0.5 * cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    vec4 sampleColor = texture(iChannel0, uv);
    vec4 sampleColor1 = texture(iChannel0, uv);
    sampleColor.rgb = vec3((sampleColor.r * 0.3 + sampleColor.g * 0.59 + sampleColor.b * 0.11));
    sampleColor.rgb = smoothstep(0.555,.588,sampleColor.rgb) * smoothstep(0.555,.588,sampleColor.rgb);

    float scale = 8.;
    float scale1 = 8.;
    float scale2 = 8.;
    fragColor =  vec4(texture(iChannel0, uv * .97 + 0.03 * clamp(0.,sin(iTime * scale + 10.),1.)).r ,
    texture(iChannel0, uv * .99 + 0.01 * clamp(0.,sin(iTime * scale + 3.),1.)).g,  
    texture(iChannel0, uv * .95 + 0.05 * clamp(0.,sin(iTime * scale + 7.),1.)).b,1.);
}
