float density = .1;
float opacityScanline = .3;
float opacityNoise = .2;
float flickering = 0.03;

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = texture(	iChannel0,uv).rgb;


    float count = iResolution.y * density;//数量=长度*密度

    vec3 scanlines;
    if(uv.x<0.5){
        vec2 s1 = vec2(sin(uv.y * count), cos(uv.y * count));
        scanlines = vec3(s1.x, s1.y, s1.x);
    }else{
        //对比正弦
        float s2 = sin(uv.y * count);
        scanlines = vec3(s2);
        //对比余弦
        //vec2 s2 = vec2(sin(uv.y * count), cos(uv.y * count));
        //scanlines = vec3(s2.y, s2.y, s2.y);
    }



    col += col * scanlines * opacityScanline;
    col += col * vec3(random(uv*iTime)) * opacityNoise;
    col += col * sin(110.0*iTime) * flickering;

    fragColor = vec4(col,1.0);
}
