vec2 tg(vec2 p, float s)
{
    return vec2(floor(p.x / s) * s, floor(p.y / s) * s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float mc = max(iResolution.x, iResolution.y);
    vec2 uv = fragCoord/mc;
    vec4 m = iMouse / mc;

    vec2 gp = tg(uv , 0.01);

    float d;

    d = (distance(m.xy, gp) * distance(abs(m.zw), gp)) * 25.;

    vec3 col = vec3(
    sin(d + (iDate.w * 0.3)), 
    cos(d + (iDate.w * 0.4)), 
    sin(d + (iDate.w * 0.5)));

    fragColor = vec4(col,1.0);
}
