/* Source: https://www.shadertoy.com/view/ltXSDB */
// Test if point p crosses line (a, b), returns sign of result
float testCross(vec2 a, vec2 b, vec2 p) {
    return sign((b.y-a.y) * (p.x-a.x) - (b.x-a.x) * (p.y-a.y));
}
// Determine which side we're on (using barycentric parameterization)
float signBezier(vec2 A, vec2 B, vec2 C, vec2 p)
{ 
    vec2 a = C - A, b = B - A, c = p - A;
    vec2 bary = vec2(c.x*b.y-b.x*c.y,a.x*c.y-c.x*a.y) / (a.x*b.y-b.x*a.y);
    vec2 d = vec2(bary.y * 0.5, 0.0) + 1.0 - bary.x - bary.y;
    return mix(sign(d.x * d.x - d.y), mix(-1.0, 1.0, 
        step(testCross(A, B, p) * testCross(B, C, p), 0.0)),
        step((d.x - d.y), 0.0)) * testCross(A, C, B);
}
/**/

void mainImage( out vec4 O, in vec2 SC )
{
    float offs = sinOSC(-0.5, 0.5, iTime * 0.5);
    vec2 NDC = TO_CENTERED_NDC(SC) * 2.0;

    vec2 P0 = vec2(-2.0, 0.0) * 0.65; 
    vec2 P1 = vec2(-2.0, 0.0);
    vec2 P2 = vec2(2.0, 0.0) * 0.65;

    P0 = rotateAroundZ(P0, iTime * 0.1) + vec2(2.*offs, -offs);
    P1 = rotateAroundZ(P1, iTime * 0.9) + vec2(-6.*offs, 0.0);
    P2 = rotateAroundZ(P2, iTime * 0.1) + vec2(-offs, offs);

    // Without sign.
    float minDist = quadraticBezierSegmentSDF_L2(NDC, P0, P1, P2);

    // With sign.
    //float minDist = quadraticBezierSegmentSDF_L2(NDC, P0, P1, P2) * signBezier(P0, P1, P2, NDC);

    // Blending.
    vec3 color = colorize_field(minDist, vec3(0.1, 0.4, 0.7));
    color = add_metric_circle(NDC, P0, P1, P2, color);
    #define UNIT ( 8./R.y )
    color = mix( color, vec3(1,0,0), smoothstep(UNIT, 0., diskSDF_L2(NDC, P0, 2.*UNIT)) );
    color = mix( color, vec3(0,1,0), smoothstep(UNIT, 0., diskSDF_L2(NDC, P1, 2.*UNIT)) );
    color = mix( color, vec3(0,0,1), smoothstep(UNIT, 0., diskSDF_L2(NDC, P2, 2.*UNIT)) );

    O = vec4(color, 1.0);
}

/* Returns sine modulated value in range [min, max]. */
float sinOSC(in float min, in float max, in float T)
{
    float v = (sin(T) + 1.0) * 0.5; // map T to [0.0, 1.0];
    return min + v * (max - min);   // map T to [min, max];
}

vec2 rotateAroundZ(vec2 p, float a)
{
    float s = sin(a);
    float c = cos(a);

    return vec2(
        p.x * c - p.y * s,
        p.y * c + p.x * s);
}

float diskSDF_L2(vec2 NDC, vec2 C, float r)
{
    return length(C - NDC)-r;
}

/*
    Calculates cube root with Newton-Raphson method.
*/
float cubeRoot(const float x)
{
    float xc = x;
    float xxc = x * x;

    for (int i = 0; i < 2; ++i)
    {
        xc = (2.0 * xc + x / xxc) * 0.33333333333333331;
        xxc = xc * xc;
    }

    return xc;
}

/*
    Return value:
        0 - two complex roots;
        1 - one real root, but root[0] == root[1];
        2 - two real roots;
*/
int solveQuadratic(
    inout vec2[2] roots,
    in float[3] coefs)
{
    float discriminant = coefs[1] * coefs[1] - 4.0 * coefs[0] * coefs[2];

    if (discriminant < 0.0)
    {
        float INVa = 1.0 / (2. * coefs[0]);
        float SQRT = sqrt(-discriminant);

        roots[0].x = -coefs[1] * INVa;
        roots[0].y = SQRT * INVa;
        roots[1].x = -coefs[1] * INVa;
        roots[1].y = -SQRT * INVa;

        return 0;
    }

    if (discriminant == 0.0)
    {
        float Re = -coefs[1] / (2. * coefs[0]);

        roots[0].x = Re;
        roots[0].y = 0.0;
        roots[1].x = Re;
        roots[1].y = 0.0;

        return 1;
    }

    float INVa = 1.0 / (2. * coefs[0]);
    float SQRT = sqrt(discriminant);

    roots[0].x = (SQRT - coefs[1]) * INVa;
    roots[0].y = 0.0;
    roots[1].x = (-coefs[1] - SQRT) * INVa;
    roots[1].y = 0.0;

    return 2;
}

/*
    Return value:
        1 - one real, two complex roots;
        2 - three real roots, but root[1] == root[2];
        3 - three real roots.
*/
int solveCubicVieta(
    inout vec2[3] roots,
    in float[4] coef)
{
    const float oneThird = 0.33333333333333331;
    const float TAU = 6.2831853071795862;

    float a = coef[1] / coef[0];
    float b = coef[2] / coef[0];
    float c = coef[3] / coef[0];

    float Q = (a * a - 3.0 * b) / 9.0;
    float R = (2.0 * a * a * a - 9.0 * a * b + 27.0 * c) / 54.0;

    float S = Q * Q * Q - R * R;

    if (S > 0.0)
    {
        float phi = oneThird * acos(R / sqrt(Q * Q * Q));
        float Re = -2.0 * sqrt(Q);

        roots[0].x = Re * cos(phi) - a * oneThird;
        roots[0].y = 0.0;
        roots[1].x = Re * cos(phi + TAU * oneThird) - a * oneThird;
        roots[1].y = 0.0;
        roots[2].x = Re * cos(phi - TAU * oneThird) - a * oneThird;
        roots[2].y = 0.0;

        return 3;
    }

    if (S < 0.0 && Q > 0.0)
    {
        float phi = oneThird * acosh(abs(R) / sqrt(Q * Q * Q));
        float signR = R < 0.0 ? -1.0 : 1.0;

        float sqrtQ = sqrt(Q);
        float Re = signR * sqrtQ * cosh(phi);
        float Im = sqrtQ * sinh(phi);
        float sqrt3 = 1.7320508075688772;

        roots[0].x = -2.0 * Re - a * oneThird;
        roots[0].y = 0.0;
        roots[1].x = Re - a * oneThird;
        roots[1].y = sqrt3 * Im;
        roots[2].x = Re - a * oneThird;
        roots[2].y = -sqrt3 * Im;

        return 1;
    }

    if (S < 0.0 && Q < 0.0)
    {
        float phi = oneThird * asinh(abs(R) / sqrt(abs(Q * Q * Q)));
        float signR = R < 0.0 ? -1.0 : 1.0;

        float sqrtQ = sqrt(abs(Q));
        float Re = signR * sqrtQ * sinh(phi);
        float Im = sqrtQ * cosh(phi);
        float sqrt3 = 1.7320508075688772;

        roots[0].x = -2.0 * Re - a * oneThird;
        roots[0].y = 0.0;
        roots[1].x = Re - a * oneThird;
        roots[1].y = sqrt3 * Im;
        roots[2].x = Re - a * oneThird;
        roots[2].y = -sqrt3 * Im;

        return 1;
    }

    if (Q == 0.0)
    {
        //float Re = -cubeRoot(c - a * a * a / 27.0) - a / 3.0;
        float Re = -pow(c - a * a * a / 27.0, 1./3.) - a / 3.0;
        float Im = sqrt(abs(a - 3.0 * Re) * (a + Re) - 4.0 * b) * 0.5;

        roots[0].x = Re;
        roots[0].y = 0.0;
        roots[1].x = -(a + Re) * 0.5;
        roots[1].y = Im;
        roots[2].x = -(a + Re) * 0.5;
        roots[2].y = Im;

        return 1;
    }

    float signR = R < 0.0 ? -1.0 : 1.0;
    float Re = signR * sqrt(Q);

    roots[0].x = -2.0 * Re - a * oneThird;
    roots[0].y = 0.0;
    roots[1].x = Re - a * oneThird;
    roots[1].y = 0.0;
    roots[2] = roots[1];

    return 2;
}

/*
    Returns number of finded real roots.
*/
int solve(
    inout vec2[3] roots,
    in float[4] coef)
{
    // Quadratic equation?
    if (coef[0] == 0.0)
    {
        // Linear equation?
        if (coef[1] == 0.0)
        {
            // Constant?
            if (coef[2] == 0.0) { return 0; }

            // Solve linear.
            roots[0].x = -coef[3]/coef[2];
            roots[0].y = 0.0;

            return 1;
        }

        vec2 r[2];
        float c[3] = float[3](
            coef[1],
            coef[2],
            coef[3]
        );

        int num = solveQuadratic(r, c);
        roots[0] = r[0];
        roots[1] = r[1];

        return num;
    }

    return solveCubicVieta(roots, coef);
}
