vec2 triangle_wave(vec2 a){
    vec2 to_return = abs(fract((a+vec2(1.,0.5))*1.5)-.5);
    return to_return;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.5*3.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float scale = 1.5;
    float offset = 0.;
    float offset1 = iTime/1000.;
        vec2 t2 = vec2(0.);
        vec2 t3 = vec2(0.);
        for(int k = 0; k < 9; k++){
            float p1 = sign(uv.x);
            uv += t2;
            uv /= scale;
            t2 = p1*triangle_wave(uv-.5);
            t3 = -p1*triangle_wave(uv.yx);
            uv = t2+t3;
            col.x = min(uv.x-uv.y+col.x*p1,-p1*col.x/3.);
            col = abs(col.yzx-vec3(col.x)/3.);
        }

    fragColor = vec4(col,1.0);
}
