void mainImage(out vec4 fragColor, in vec2 fragCoord){

vec2 c = fragCoord.xy - iResolution.xy / 2. ; // [+-iRes.x/2] x [+-iRes.y/2]
float dist = sqrt( dot(c,c) ); // [0 ~ inf]
float pi = 3.141592;
float ang = atan( c.y , c.x ); // [-pi ~ pi]
dist = pow(dist,0.35);

float bg = ceil(sin(11.*ang)+0.);
bg = clamp(bg, 0.,1.);
float allboxes=0., box, f;
for(int i = 1; i < 12;i ++ ){
    f = float(i);
    box = 0.;
    if( (cos( ang + f*pi/5.5) ) > 0.94 ) box = 1.; // hack to make this work
    box = clamp(box,0.,1.);
    // box = abs(box);
    box *= clamp(ceil(+0.7+sin(dist*0.5 - iTime + 8.*fract(f/2.6))+0.15),0.,1.); //other hack to make this work
    allboxes += box;
}

fragColor = vec4( 1.-allboxes , 0.0 , 0.0 , 1.0);
}
