void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;
    vec2 uv1 = ((fragCoord)*10.0-iResolution.xy)/iResolution.y/10.;
    uv1 += iTime/10.;
    for(int c=0;c<3;c++){
        vec2 uv = uv1;
        for(int i=0;i<6;i++)
        {
            uv = fract((uv.y+uv.x+uv)/1.5)*1.5;
            uv *= (uv.x-uv.y);
            col += col.yzx*(uv.x+uv.y)/8.;
            col[c] += (uv.y-uv.x);
        }

	}

    fragColor = vec4(fract(col),1.0);

}
