#define tau 6.28318530718
#define pi 3.141592653589

//i was too lazy to wobble on my own
//https://www.shadertoy.com/view/WsKXzz

float wobble(float p, float amplitude, float frequence, float speed,float time)
{
    return amplitude * sin(p * frequence + time * speed);
}

vec2 wobble2d(vec2 uv, float a, float f, float s,float time)
{
    return vec2(wobble(uv.y, a, f, s,time), wobble(uv.x, a, f, s,time + pi*.5));
}

float caustics(vec2 uv,float time,sampler2D tex)
{
    vec2 uv1 = uv + wobble2d(uv, .1, 2., 2.,time);
    float c1 =1.-texture(tex,uv1).r;
    vec2 uv2 = .5 *uv + .5*wobble2d(uv, .1, 2., 2.,time*.5);
    float c2 =1.-texture(iChannel0,uv2).r;
    return pow((c1 +c2) *.5,8.)*2.;
}

float causticsRec(vec2 uv,float time,sampler2D tex)
{
    float mid1 = caustics(1.-uv*.5 +time * .01,time *.5,tex);
    float mid2 = caustics(1.+uv*.5 +time * .01,time *.5,tex);
    vec2 mid  = mix(uv,vec2(mid1,mid2),.28);
    return caustics(mid+time * .01 ,time + mid1,tex);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xx ;
    uv = uv *2.;


    float c = causticsRec(uv,iTime,iChannel0);

    c = (sqrt(c) + c)/ 2.;

    vec3 col = vec3(.1,.4,.8);
    col +=.25;
    col *= texture(iChannel1, uv*.5 + wobble2d(uv, .004, 8., 2., iTime)).xyz;

    vec3 foam = mix(vec3(0),texture(iChannel2,uv *.5  + wobble2d(-uv, .05, 8., 2., iTime)).xxx,c);

    fragColor = vec4(col + foam,1);
}
