float radius  = 0.50f;
float centerX = 0.33f;
float centerY = 0.33f;
float blur = 0.2f;

float DrawCircle(vec2 uv, vec2 center, float radius, float blur) 
{    
    float d = length(center-uv);
    float col = smoothstep(radius, radius-blur, d);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy; 
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 1.5;

    vec2 center = vec2(centerX * cos(iTime), centerY * sin(iTime) );
    vec2 center2 = vec2(-centerX * cos(iTime), -centerY * sin(iTime) );

    float col = 0.0f;
    col += DrawCircle(uv, center, radius, blur);
    col += DrawCircle(uv, center2, radius, blur);


    vec4 mat = texture(iChannel0, uv);    
    fragColor = mat*col;
}
