void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord/iResolution.xy;
    float mouse = iMouse.x/iResolution.x;

    uint bit_mask = (iMouse.z > 0.0) ? uint(exp2(mouse*8.0)) : uint(exp2(fract(0.1*iTime)*8.0)); 

    vec4 tex = texture(iChannel0, uv) * 255.0;
    uvec4 tex_int = uvec4(uint(tex.r), uint(tex.g), uint(tex.b), uint(tex.a));

    tex_int = tex_int & bit_mask;

    tex = vec4(float(tex_int.r), float(tex_int.g), float(tex_int.b), float(tex_int.a));
    tex /= float(uint(0xffffff) & bit_mask); // scaling back to 0-1 range

    fragColor = tex;
}
