float Circle(vec2 uv,vec2 p, float r, float blur)
{
    float d = length(uv-p);
    float c = smoothstep(r, r-blur,d);
    return c;
}

float CoorUvX()
{
    return -cos(iTime/3.)*1.5+1.2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    float mask = Circle(uv, vec2(CoorUvX(),0.5), 0.07,0.005);

    float decalRebord = 0.1;
    float coeffRapid = 4.5;
    if(iTime >2.2)
    {
        mask += Circle(uv, vec2( floor( (decalRebord+CoorUvX() )*coeffRapid)/coeffRapid-decalRebord, 0.5 ),0.07,0.005);
    }

    //mask += Circle(uv, vec2( floor( (CoorUv(iTime)).x), 0.5) ,0.08,0.01);
    // Output to screen

    vec3 vecColor = vec3(0.,0.,0.);

    if(iTime < 6.)
    {
        mask = mask;
    }
    else if(6.<=iTime && iTime < 10.07)
    {
        mask = (0.,0.,0.);
        vecColor = texture(iChannel0,uv-vec2(0.5,-0.2)).rgb *vec3(Circle(uv-0.5, vec2(0.2-cos((iTime+6.)/3.5)*1.5,0.), 0.24,0.005));
    }
    else if(10.07<iTime && iTime < 11.5)
    {
        mask = (0.,0.,0.);
        vecColor = texture(iChannel0,uv-vec2(0.5,-0.2)).rgb *vec3(Circle(uv-0.5, vec2(0.3865,0.), 0.24,0.005));

    }
    else if(11.5<iTime && iTime < 15.5)
    {
        mask = Circle( uv-0.5, vec2(0.3865,0.), 0.24,0.005 )*((iTime-11.5)/4.,0.+(17.5-iTime)/6.,0.+(17.5-iTime)/4.);
        vecColor = vec3((iTime-11.5)/4.,0.,0.);
        //

    }
    else if(15.5<iTime && iTime < 20.)
    {
        mask = (1.,0.,0.);
        vecColor = vec3(1.,0.,0.);
    }
    else if(20.<iTime && iTime < 22.)
    {
        mask = (1.,0.,0.);
        vecColor = texture(iChannel0,uv).rgb;
    }
    else
    {
        mask = (0.,0.,0.);
    }
    fragColor = vec4(vec3(mask)+vecColor,1.0);
}
