#define N 10
#define ZOOM 1.
#define SEED 5



vec2 roots(int offset) {
    if (offset == -1) return iMouse.xy;

    float s = rand(offset);
    return vec2(0.5+sin(iTime)*rand(SEED*offset), 0.5+cos(iTime)*rand(SEED*offset+1));
}


void mainImage(out vec4 O, in vec2 Q) {
    vec2 z = SS(Q);

    float nearestDist = 100.0;
    int nearestIndex = 0;
    for (int i = 0; i < N; i++) {
        float dist = cms(z - roots(i));
        if (dist < nearestDist) {
            nearestDist = dist;
            nearestIndex = i;
        }
    }

    vec3 col = vec3(roots(nearestIndex), 0.0);
    for (int i = 0; i < N; i++) {
        col = draw_point(col, z, roots(i), 0.00001);
    }

    O = vec4(col, 1.0);
}

#define R iResolution.xy
#define SS(p) (2. * p / R.xy - vec2(0.5))

float cms(vec2 c) { 
    return c.x * c.x + c.y * c.y; 
}

vec3 draw_point(vec3 col, vec2 pos, vec2 pt, float r){
    return cms(pos - pt) < r ? vec3(1) : col;
}

float rand(int seed){
    return fract(sin(float(seed) * 78.233) * 43758.5453);
}
