vec2 triangle_wave(vec2 a){
    return abs(fract((a+vec2(1.,0.5))*1.5)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.5*4.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0 + vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    vec2 t2 = vec2(0.);
    for(int k = 0; k < 9; k++){
        float p1 = sign(uv.x);
        uv = (uv+t2/1.5)/(1.5+p1*.5);

        //if(uv.y>uv.x) uv = uv.yx; else uv *= -sign(uv.y-uv.x);
        t2 = -p1*triangle_wave(uv-.5);
        uv = t2-p1*triangle_wave(uv.yx);

        col.x = max(uv.y-uv.x,col.x/4.)+col.x-.5;
        col = abs(col.yzx-col.x/(4.));
    }
    fragColor = vec4(col*3.,1.0);
}
