void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy)/iResolution.y;
    float f = texture(iChannel0, vec2(0.)).x;
    float R = mod(abs(sin(length(10. * uv) - 5. * f - 5. * iTime)), 2.);
    float d = f * .05/length(uv) + .05/abs(R);
    fragColor = vec4(d);
}
