void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    float fontHeight = 30.0;
    float fontWidth = 15.0;
    float fontRatio = fontWidth / fontHeight;
    vec2 uv = (fragCoord - iResolution.xy + vec2(fontWidth, fontHeight)) / fontHeight;

    vec2 offset = vec2(1.0 / 16.0, 1.0 / 16.0);
    vec2 digit = vec2(0.0, -4.0);
    vec2 decimalPoint = vec2(-2.0, -3.0);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec4 font = vec4(0.0);

    // float number = 1234.09999;
    float number = 1.0 / iTimeDelta;
    float decimalPlaces = 2.0;
    number *= pow(10.0, decimalPlaces);
    float i = 0.0;
    while (number > 0.999) {
        vec2 coords = uv + vec2(i * fontRatio + (1.0 - fontRatio), 0.0);
        if (abs(i - decimalPlaces) <= 0.001) {
            if (coords.x >= (1.0 - fontRatio) / 2.0 && coords.x < 1.0 - (1.0 - fontRatio) / 2.0 && coords.y >= 0.0 && coords.y < 1.0) {
                font = texture(iChannel0, (coords + decimalPoint) * offset);
            }
            i += 1.0;
            continue;
        } else {
            float current = mod(floor(number), 10.0);
            if (coords.x >= (1.0 - fontRatio) / 2.0 && coords.x < (1.0 + fontRatio) / 2.0 && coords.y >= 0.0 && coords.y < 1.0) {
            //if (true) {
                font = texture(iChannel0, (coords + digit + current * vec2(1.0, 0.0)) * offset);
            }
            number /= 10.0;
        }
        i += 1.0;
    }
    // Output to screen
    fragColor = vec4(font.x, 0.0, 0.0, 1.0);
}
