// Author: Ferran de Blas
// Title: First shader
// Objective: Display a circle where the mouse cursor is. The pixels close to the mouse position will have a stronger color, resulting in a transition from pure white to pure black in pixels far away from the cursor.

#ifdef GL_ES
precision mediump float;
#endif



float distanceBetweenPoints(vec2 p1, vec2 p2){
    float x = pow(p2.x-p1.x,2.0);
    float y = pow(p2.y-p1.y,2.0);

    return sqrt(x+y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 st = fragCoord.xy/iResolution.xy;
    vec2 mouse_pos = iMouse.xy/iResolution.xy;

    float aspect_ratio = iResolution.x / iResolution.y;
    mouse_pos.x *= aspect_ratio;
    st.x *= aspect_ratio;

    float distance = distanceBetweenPoints(st,mouse_pos);

    float color = 1.0-(distance*4.0);

	fragColor = vec4(color,color,color,1.0);
}
