void circle(inout vec4 fragColor, vec2 uv, vec2 c, float r, vec3 col)
{
    float l = length(uv.xy - c.xy);
    if (l < r) fragColor += vec4(col.rgb, 1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float ratio = iResolution.x / iResolution.y;
    uv.x *= ratio;
	vec3 color = vec3 (0.7, 0.07, 0.1);
    vec2 c = vec2(0.5, 0.5);
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    circle(fragColor, uv, c, 0.2, color);
}
