void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col1 = texture(iChannel0, uv).xyz;
    vec3 col2 = texture(iChannel1, uv).xyz;

    vec3 col = mix(col1, col2, sin(iTime * 2.0 + uv.x * 5.0) / 2.0 + 0.5);
    fragColor = vec4(col, 1.0);
}

float f (float n, vec3 hsl)
{
    float k = mod((n + hsl.x / 30.0), 12.0);
    float a = hsl.y * min(hsl.z, 1.0 - hsl.z);

    return hsl.z - a * max(-1.0, min(min(k - 3.0, 9.0 - k), 1.0));
}

vec3 hslToRgb (vec3 hsl)
{
    return vec3(f(0.0, hsl), f(8.0, hsl), f(4.0, hsl));
}

vec3 hslToRgb (float h, float s, float l)
{
    return hslToRgb(vec3(h, s, l));
}

float brightness (float r, float g, float b)
{
    return 0.2126 * r + 0.7152 * g + 0.0722 * b;
}

float brightness (vec3 rgb)
{
    return brightness(rgb.x, rgb.y, rgb.z);
}

vec3 grayscale (float r, float g, float b)
{
    return vec3(brightness(r, g, b));
}

vec3 grayscale (vec3 rgb)
{
    return grayscale(rgb.x, rgb.y, rgb.z);
}

vec3 mix (vec3 col1, vec3 col2, float strength)
{
    return vec3(col1.x * strength + col2.x * (1.0 - strength), col1.y * strength + col2.y * (1.0 - strength), col1.z * strength + col2.z * (1.0 - strength));
}
