        Col = vec3(sin(iTime), sin(iTime + length(uv)), cos(iTime * 0.4f));
        vec2 Position = vec2(0, 0);
        Col += distance(uv, Position);
        vec2 GradienceDir = vec2(0, 0.3f);
        float Intensity = 0.6f;
        Col *= sin(iTime);
