float N21(vec2 p)
{
    p = fract(p * vec2(6574.5414, 8961.8778));
    p += dot(p, p + 23.45);
    return fract(p.x * p.y);
}
vec2 N22(vec2 p)
{
    float x = N21(p);
    float y = N21(p + x);
    return vec2(x, y);
}
vec2 GetPos(vec2 id)
{
    vec2 n = N22(id) * 24.;
    return sin(n) * .4;
}
float sn(vec2 uv)
{
    vec2 lv = fract(uv * 10.0);
    vec2 id = floor(uv * 10.0);

    lv = lv * lv * (3. - 2. * lv);

    float bl = N21(id);
    float br = N21(id +vec2(1, 0));
    float b = mix(bl, br, lv.x);

    float tl = N21(id + vec2(0,1));
    float tr = N21(id +vec2(1, 1));
    float t = mix(tl, tr, lv.x);

    return mix(b, t, lv.y);
}
float stars(vec2 uv)
{
    vec2 gv = fract(uv) - 0.5;
    vec2 id = floor(uv);
    vec2 pos = GetPos(id);
    float distance = length(gv - pos);
    float shineRate = abs(sin(iTime * 0.8 * N21(id)));

    float star = smoothstep(0.06, 0.059, distance) * shineRate;
    return star - 0.15;
}
float circleMask(vec2 uv, float radius)
{
    float vL = length(uv);
    float circle = smoothstep(radius, radius - 0.04, vL);
    return circle;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    float sunCircle = circleMask(uv + vec2(0.5, -0.3), 0.07) * 3.0;
    vec3 sunColor = sunCircle * vec3(0.9, 0.7, 0.2);

    float band = dot(uv, vec2(3.0, -8.0)) / 2.0;
    float stars = stars(uv * 60.0);
    float mixed = mix(band, stars, 1.0 - clamp(band, 0.0, 1.0));
    vec3 bandColor = vec3(1.0, 0.2, 0.7) * mixed;


    fragColor = vec4(bandColor + sunColor, 1.0);
}
