//unfortunately float precision breaks down eventually :(

float n = 3000.;
vec2 t = vec2(-.5049203,.5162320);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y*4.-vec2(2.*iResolution.x/iResolution.y,2.);
    uv = uv/pow(2.,iTime)+t;
    vec2 z = vec2(0);
    float d = 0.;
    for(float i = 0.; i<n; i++)
    {
        d++;
        z = vec2(z.x*z.x-z.y*z.y+uv.x,2.*z.x*z.y+uv.y);
        if(length(z)>2.)
            break;
    }
    vec3 col;
    if(d == n) col = vec3(0.);
    else col = vec3(vec2(mod(d/20.,1.)),1);

    fragColor = vec4(col,1.0);
}
