// https://www.shadertoy.com/view/7sSczG yet another sierpinski triangle by jt
void mainImage(out vec4 o, vec2 p)
{
    p /= iResolution.xy;
    vec3 q =
        min
        (
            // barycentric coordinates (somewhat modified)
            vec3
            (
                1. - p.x + .5 * p.y,
                   + p.x + .5 * p.y,
                1.             -p.y
            ),
            1.0 // remove outside of main triangle
        );

    ivec3 i = ivec3(q*64.);
    o = vec4(i.x & i.y & i.z); // inspired by FabriceNeyret2's shortest Sierpinski https://www.shadertoy.com/view/ldlfRM
}
