void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy/0.5;

    float cycleTime = 4.0f;
    float halfCycleTime = cycleTime * 0.5f;
    float rowHalfWidth = 0.005f;
    float bias = -1.0f;

    float cyclePosition = mod(float(iTime+uv.x), cycleTime);

    vec4 rowColor = vec4(1.f, 1.f, 1.f, 1.f);

    if (cyclePosition < halfCycleTime - rowHalfWidth) {
        fragColor = texture(iChannel0, uv, bias);
    } else if (
        (cyclePosition >= halfCycleTime - rowHalfWidth && cyclePosition < halfCycleTime + rowHalfWidth)
        ||
        (cyclePosition < rowHalfWidth || cyclePosition > cycleTime - rowHalfWidth)
    ){
        fragColor = rowColor;
    } else {
        fragColor = texture(iChannel0, uv);
    }
}
