}

vec2 mainSound(int samp, float time){
  float s1 =
      8.,
  s2 = 4.,
  t = time,
  m1 =
      0.
  ;
  t /= 2.;
  for(int i = 0; i < 3; i++){

      t *= 1.+fmod(t/2.,2.);
      t += floor(-t/2.);
      m1 =
      fmod(-t*2.,s2);

      t = t/(1.+fmod(m1+floor(t*2.)+floor(t/2.),2.));
  }
  float
  m3 =
      fmod((t),s1/2.)
  ;
  float m2 =
      1. + floor(fmod(t,s1/2.));
  ;
  float a=
      pow((sqrt(fract(-t))),2.)*8.
  ;
  float nb = pow(2.,(m3+m2-m1)/5.+7.)*2.;
  return
      abs(.5-abs(vec2(sin(time*nb*.998)*a,sin(time*nb)*a)))*2.
  ;
}
