void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(texture(iChannel0, uv));
}

#define X_SPEED 0.3
#define Y_SPEED 0.7

#define LINE_SPEED 1.

#define SIN_ADD 0.2

#define SIZE 0.01
#define SCALE 0.4

#define FADE 0.01
#define FADE_LIMIT 1.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //Center UV
    vec2 cuv = uv - vec2(.5);
    cuv.x *= iResolution.x/iResolution.y;

    float t = iTime*LINE_SPEED;

    //backbuffer texture
    vec4 draw = texture(iChannel0, uv);

    vec3 col;

    //Blend with backbuffer
    if (distance(draw.rgb, vec3(0.)) > FADE_LIMIT) {
        col = draw.rgb - vec3(FADE);
    } else {
        col = draw.rgb;
    }

    float path_rad = (sin(t)+1.)/2.+SIN_ADD;
    path_rad *= SCALE;

    vec2 p = vec2(
        cos(t*X_SPEED)*path_rad,
        sin(t*Y_SPEED)*path_rad
    );

    // Draw point
    if (distance(cuv, p) < SIZE) {
        col = vec3(p/SCALE/2., path_rad);
    }

    fragColor = vec4(col, 1.);
}
